
/**
 * Merge two maps into one. Similar to Object.assign({}, map1, map2) (but for maps)
 * @param {Map} map1
 * @param {Map} map2
 * @return {Map} A fresh map that
 */
export const merge = (map1, map2) => {
  const merged = new Map()
  const merge = (value, key) => { merged.set(key, value) }
  map1.forEach(merge)
  map2.forEach(merge)
  return merged
}

export const writeTo = (source, target) =>
  source.forEach((value, key) => { target.set(key, value) })

export const createMap = () => new Map()
